 var envVars = EnvironmentVariables();

 string path;
 if (envVars.TryGetValue("PATH", out path))
 {
     Information("Path: {0}", path);
 }

 foreach (var envVar in envVars)
 {
     Information(
         "Key: {0}\tValue: \"{1}\"",
         envVar.Key,
         envVar.Value
         );
 }

