Task("Sign")
    .IsDependentOn("Clean")
    .IsDependentOn("Restore")
    .IsDependentOn("Build")
    .Does(() =>
{
    var file = "Core.dll";
    Sign(file, new SignToolSignSettings {
            TimeStampUri = new Uri("http://timestamp.digicert.com"),
            CertPath = "digitalcertificate.pfx",
            Password = "TopSecret"
    });
});

Task("Sign")
    .IsDependentOn("Clean")
    .IsDependentOn("Restore")
    .IsDependentOn("Build")
    .Does(() =>
{
    var file = new FilePath("Core.dll");
    Sign(file, new SignToolSignSettings {
            TimeStampUri = new Uri("http://timestamp.digicert.com"),
            CertPath = "digitalcertificate.pfx",
            Password = "TopSecret"
    });
});

